(function() {
    'use strict';
    var User = require('../models/user.model');
    var nodemailer = require('../../node_modules/nodemailer');
    var Transaction = require('../models/transaction.model');

    exports.newUser = function(request, response) {
        console.log(request.body);

        var user = new User({
            "fullname": request.body.fullname,
            "cpfcnpj": request.body.cpfcnpj,
            "email": request.body.email,
            "phone": request.body.phone,
            "login": request.body.login,
            "password": request.body.password
        });

        user.save(function(error) {
            if (error) response.status(500).send(error);
            else response.status(200).send("user registered");
        });

    };

    exports.getUsers = function(req, res, next) {
        User.find(function(err, users) {
            if (err) return next(err);
            res.json(users);
        });
    };

    exports.logUser = function(req, res, next) {

        User.find(function(err, users) {
            if (err) return next(err);
            else {
                var user;

                for (var i = 0; i < users.length; i++) {

                    if (users[i].login == req.body.login) {

                        if (users[i].password == req.body.password) {
                            user = users[i];
                            user.id = user._id;

                            res.status(200).send(user);

                            return;
                        }
                    }
                }
                res.status(400).send("user not found");
            }

        });
    };


    exports.sendinvoice = function(req, res, next) {

        var transporter = nodemailer.createTransport('smtps://inpayapp@gmail.com:1ngenico@smtp.gmail.com');

        Transaction.find(function(err, transactions) {

            if (err) return next(err);

            else {
                var userTransaction;

                for (var i = 0; i < transactions.length; i++) {

                    if (transactions[i]._id == req.body.transactionId) {
                        userTransaction = transactions[i];
                    }
                }

                if (userTransaction) {
                    send_invoice(userTransaction, req.body.userMail);
                } else res.status(400).send("Transactions not found");
            }

        });

        function send_invoice(userTransaction, userMail) {
            var mailOptions = {
                from: 'inpayappÃŸ@gmail.com', // sender address
                to: userMail, // list of receivers
                subject: 'Nota fiscal', // Subject line
                html: '<html><head></head><body><div style="padding-top: 8px; font-size:30px;background-color: #FFFFCC; margin-top: 5px; margin-left: 8px; text-align: center; height: 80%" >' + '<img src="data:image/png;base64,">' + '<br><br> OperaÃ§Ã£o:' + userTransaction.choice + '<br> Data: ' + new Date(userTransaction.transactionDate).toLocaleString() + '<br> Total: ' + userTransaction.amount/100 + '<br> Parcelas: ' + userTransaction.portionsNumber + '</div></body></html>' // html body
            };

            transporter.sendMail(mailOptions, function(error, info) {
                if (error) {
                    return console.log(error);
                }
                console.log('Message sent: ' + info.response);
            });
            res.status(200).send(userMail);
        }

    };

    exports.setPassword = function(req, res, next) {

        var transporter = nodemailer.createTransport('smtps://inpayapp@gmail.com:1ngenico@smtp.gmail.com');

        User.find(function(err, users) {
            if (err) return next(err);
            else {
                var user;

                for (var i = 0; i < users.length; i++) {
                    if (users[i].cpfcnpj == req.body.cpfcnpj) {

                        user = users[i];
                        user.password = Math.floor((Math.random() * 10000));

                        save_user(user);
                        return;
                    }
                }

                res.status(400).send("user not found");
            }

        });


        function save_user(user) {
            return user.save(function(error) {
                if (error) res.status(500).send(error);
                else {
                    var mailOptions = {
                        from: 'inpayappÃŸ@gmail.com', // sender address
                        to: user.email, // list of receivers
                        subject: 'RecuperaÃ§Ã£o de senha', // Subject line
                        html: '<b>OlÃ¡ ' + user.fullname + ', sua senha foi alterada para: ' + user.password + '</b>' // html body
                    };

                    transporter.sendMail(mailOptions, function(error, info) {
                        if (error) {
                            return console.log(error);
                        }
                        console.log('Message sent: ' + info.response);
                    });
                    res.status(200).send(user.email);
                }
            });
        }
    };

    exports.updateProfile = function(req, res, next) {


        User.find(function(err, users) {
            if (err) return next(err);
            else {
                var user;
                for (var i = 0; i < users.length; i++) {
                    if (users[i]._id == req.body.id) {

                        user = users[i];
                        if (req.body.fullname !== undefined)
                            user.fullname = req.body.fullname;
                        if (req.body.email !== undefined)
                            user.email = req.body.email;
                        if (req.body.phone !== undefined)
                            user.phone = req.body.phone;

                        save_user(user);
                        return;
                    }
                }



                res.status(400).send("user not found");
            }

        });

        function save_user(user) {
            return user.save(function(error) {
                if (error) res.status(500).send(error);
                else {
                    res.status(200).send(user);
                }
            });
        }

    };


}());
